mapper.select:
select *
from question
where que_title LIKE CONCAT(CONCAT('%', #{content}), '%');

select *
from question
where que_course LIKE CONCAT(CONCAT('%', #{content}), '%');

select *
from question
where que_detail LIKE CONCAT(CONCAT('%', #{content}), '%');

mapper.insert:
insert into question (que_id, que_title, user_id, 
  create_time, click_count, praise_count, 
  reply_count, que_course, unread, 
  que_summary, que_detail, que_img
  )
values (#{queId,jdbcType=VARCHAR}, #{queTitle,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=VARCHAR}, #{clickCount,jdbcType=INTEGER}, #{praiseCount,jdbcType=INTEGER}, 
  #{replyCount,jdbcType=INTEGER}, #{queCourse,jdbcType=VARCHAR}, #{unread,jdbcType=INTEGER}, 
  #{queSummary,jdbcType=LONGVARCHAR}, #{queDetail,jdbcType=LONGVARCHAR}, #{queImg,jdbcType=LONGVARBINARY}
  )
