mapper.select:
select *
from question
where que_title LIKE CONCAT(CONCAT('%', #{title}), '%');

select *
from question
where que_course LIKE CONCAT(CONCAT('%', #{courseName}), '%');

select *
from question
where que_detail LIKE CONCAT(CONCAT('%', #{content}), '%');

mapper.insert:
insert into question (que_id, que_title, que_summary, 
  que_detail, user_id, create_time, 
  click_count, praise_count, reply_count, 
  que_course, unread, que_img
  )
values (#{queId,jdbcType=VARCHAR}, #{queTitle,jdbcType=VARCHAR}, #{queSummary,jdbcType=VARCHAR}, 
  #{queDetail,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, 
  #{clickCount,jdbcType=INTEGER}, #{praiseCount,jdbcType=INTEGER}, #{replyCount,jdbcType=INTEGER}, 
  #{queCourse,jdbcType=VARCHAR}, #{unread,jdbcType=INTEGER}, #{queImg,jdbcType=VARCHAR}
  )
